import React, { useState, useEffect } from "react";
import TodoForm from "./TodoForm"
import { makeStyles } from '@material-ui/core/styles';

import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Button from "@material-ui/core/Button";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Checkbox from '@material-ui/core/Checkbox';
import Avatar from '@material-ui/core/Avatar';

import IconButton from '@material-ui/core/IconButton';
import CommentIcon from '@material-ui/icons/Comment';

import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        // maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        minWidth: 600,
    },
    todolist: {
        width: '100%',
        // maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        minWidth: 600,
    },
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        minWidth: '60%',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

function TodoList() {
    const classes = useStyles();
    const [checked, setChecked] = React.useState([1]);

    const [todos, setTodos] = useState([]);

    const [todosSelected, setTodosSelected] = useState([]);
    //Track is edit clicked or not
    const [editId, setEdit] = useState(null);
    //Save input value in input box
    const [inputValue, setInputValue] = useState("");

    useEffect(() => {
        console.log("use effect one");

        const todos = JSON.parse(localStorage.getItem('todos-omni'));
        if (todos) {
            setTodos(todos);
        }
    }, []);

    useEffect(() => {
        console.log("use effect two");

        localStorage.setItem('todos-omni', JSON.stringify(todos));
    }, [todos]);

    console.log(todos);

    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleToggle = (value) => () => {
        const currentIndex = checked.indexOf(value);
        const newChecked = [...checked];

        if (currentIndex === -1) {
            newChecked.push(value);
        } else {
            newChecked.splice(currentIndex, 1);
        }

        setChecked(newChecked);
    };

    const addTodo = (todo) => {
        if (!todo.title || /^\s*$/.test(todo.title)) {
            return;
        }

        const newTodos = [todo, ...todos];

        setTodos(newTodos);
        console.log(newTodos);
    };


    return (
        <React.Fragment>

            <Button variant="contained" color="primary" onClick={handleOpen}>
                Create Todo
            </Button>

            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <div className={classes.modal}>

                        <TodoForm onClose={handleClose} onSubmit={addTodo} todos={todos} />
                    </div>
                </Fade>
            </Modal>

            <List dense className={classes.todolist}>
                {todos.map((todo, index) => {
                    const labelId = `checkbox-list-secondary-label-${todo.id}`;
                    return (

                        <ListItem key={todo.id} button>
                            <IconButton aria-label="comments">
                                <CommentIcon />
                            </IconButton>
                            <ListItemText id={todo.id} primary={todo.title} />
                            <ListItemText id={labelId} primary={todo.description} />

                            <ListItemSecondaryAction>
                                <Checkbox
                                    edge="end"
                                    onChange={handleToggle(todo.id)}
                                    checked={checked.indexOf(todo.id) !== -1}
                                />
                            </ListItemSecondaryAction>
                            <IconButton aria-label="comments">
                                <CommentIcon />
                            </IconButton>
                        </ListItem>
                    );
                })}
            </List>

        </React.Fragment>


    );
}

export default TodoList;
