import React, { useState, useEffect } from "react";

import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import { Card, CardContent, CardActions, Typography, Checkbox, Button, Paper } from '@material-ui/core';
// import { IconButton, FavoriteIcon, ShareIcon } from '@material-ui/icons';
import IconButton from '@material-ui/core/IconButton';
import FavoriteIcon from '@material-ui/icons/Favorite';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';


const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        // maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        minWidth: 600,
    },
    listItemText: {
        border: '2px solid red',
        alignItems: 'left',
        justifyContent: 'left',
    },
    todolist: {
        width: '100%',
        // maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        minWidth: 600,
    },
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        minWidth: '60%',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

const Todo =

    ({
        id,
        title,
        description,
        dueDate,
        complete,
        handleToggle,
        checked,
        todos,
        setTodos,
        completeTodo,
        removeTodo,
        editTodo,
        editId,
        handleEditChange,
        inputValue,
        setInputValue
    }) => {
        // console.log(props);
        const classes = useStyles();



        return (
            <Paper className={classes.paper}>
                <Card key={id} className={classes.root}>
                    <CardContent>
                        <Typography className={title} color="textSecondary" gutterBottom>
                            {title}
                        </Typography>
                        <Typography variant="body1" component="p">
                            {description}
                        </Typography>
                        <Typography className={classes.pos} color="textSecondary">
                            {dueDate}
                        </Typography>
                        <Typography variant="body2" component="p">
                            {complete ? <p>complete</p> : <p>not complete</p>}

                        </Typography>
                        <Checkbox
                            edge="end"
                            onChange={handleToggle(id)}
                            checked={complete}
                        />

                        <IconButton aria-label="add to favorites" onClick={() => removeTodo(id, title)}>
                            <DeleteIcon />
                        </IconButton>
                        <IconButton aria-label="add to favorites" onClick={() => handleEditChange(id, title)}>
                            <EditIcon />
                        </IconButton>

                    </CardContent>

                </Card>

            </Paper>

        );
    }

export default Todo;
