import React, { useState, useEffect } from "react";
import TodoForm from "./TodoForm";
import Todo from './Todo';
import { makeStyles } from '@material-ui/core/styles';

import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Button from "@material-ui/core/Button";


import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        // maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        minWidth: 600,
    },
    listItemText: {
        border: '2px solid red',
        alignItems: 'left',
        justifyContent: 'left',
    },
    todolist: {
        width: '100%',
        // maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        minWidth: 600,
    },
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        minWidth: '60%',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

function TodoList() {
    const classes = useStyles();
    const [checked, setChecked] = React.useState([1]);

    const [todos, setTodos] = useState([]);

    const [todosSelected, setTodosSelected] = useState([]);
    //Track is edit clicked or not
    const [editId, setEdit] = useState(null);
    //Save input value in input box
    const [inputValue, setInputValue] = useState("");

    useEffect(() => {
        console.log("use effect one");

        const todos = JSON.parse(localStorage.getItem('todos-omni'));
        if (todos) {
            setTodos(todos);
        }
    }, []);

    useEffect(() => {
        console.log("use effect two");

        localStorage.setItem('todos-omni', JSON.stringify(todos));
    }, [todos]);

    console.log(todos);

    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleToggle = (value) => () => {
        const currentIndex = checked.indexOf(value);
        const newChecked = [...checked];

        if (currentIndex === -1) {
            newChecked.push(value);
        } else {
            newChecked.splice(currentIndex, 1);
        }

        setChecked(newChecked);
    };

    const handleEditChange = (id, text) => {
console.log("handle edit channngge");
        console.log(id);
        console.log(text);

        setEdit(id);
        setInputValue(text);

        handleOpen();

        // setModalShow(true);
    };

    const addTodo = (todo) => {
        if (!todo.title || /^\s*$/.test(todo.title)) {
            return;
        }

        const newTodos = [todo, ...todos];

        setTodos(newTodos);
        console.log(newTodos);
    };

    const removeTodo = (id) => {
        const removedArr = [...todos].filter((todoId) => todoId.id !== id);

        setTodos(removedArr);
    };

    const completeTodo = (id) => {
        console.log(id);
        let updatedTodos = todos.map((todo) => {
            if (todo.id === id) {
                todo.isComplete = !todo.isComplete;
            }
            return todo;
        });
        setTodos(updatedTodos);
    };

    const editTodo = (id, title, description, dueDate, complete) => {

        console.log("editTod");

        console.log(text);


        let editTodos = todos.map((todo) => {
            if (todo.id === id) {   
                todo.title = title;
                todo.description = description;
                todo.dueDate = dueDate;
                todo.complete = complete;
            }
            return todo;
        });
        setTodos(editTodos);
        setEdit(false);
    };


    return (
        <React.Fragment>

            <Button variant="contained" color="primary" onClick={handleOpen}>
                Create Todo
            </Button>

            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <div className={classes.modal}>

                        <TodoForm onClose={handleClose} onSubmit={addTodo} todos={todos} editId={editId} editTodo={editTodo} setEdit={setEdit} />
                    </div>
                </Fade>
            </Modal>

            <Container>
                {todos.map((todo, index) => {

                    return (

                        <Todo
                            key={index}
                            id={todo.id}
                            title={todo.title}
                            description={todo.description}
                            useDate={todo.useDate}
                            complete={todo.complete}
                            handleToggle={(e) => handleToggle(e)}
                            checked={checked}
                            todos={todosSelected}
                            setTodos={setTodos}
                            completeTodo={completeTodo}
                            removeTodo={removeTodo}
                            editTodo={editTodo}
                            handleEditChange={handleEditChange}
                            editId={editId}
                            inputValue={inputValue}
                            setInputValue={setInputValue}
                        />

                    );
                })}
            </Container>

        </React.Fragment>

    );
}

export default TodoList;
